category InheritanceOverrides {

  asset Parent {

    | test_base_step
      -> other_step1

    | other_step1

    | other_test
  }

  asset Child extends Parent {

    | test_base_step
      +> other_step2,
         other_step3

    | other_step2

    | other_step3

    | other_test
      +> other_step3
  }

}

category subType{
  asset BaseAsset {

    | base_step1
  }

  asset SubAsset extends BaseAsset{
    | subasset_step2
  }

  asset OtherAsset {
    | other_step1
      -> subtype_test_assoc_field1[SubAsset].subasset_step2
  }

}

category interleavedVariables{
  asset AssetA {
    let A1 = fieldB
    let A2 = fieldB.B1()
    | testStepA
      -> A2().testStepA,
         A1().testStepB
  }

  asset AssetB {
    let B1 = fieldA
    let B2 = fieldA.A1()
    | testStepB
      -> B1().testStepA,
         B2().testStepB
  }

}

category setOperations{
  asset SetOpsAssetA {
    | testStepSetOpsA
      -> (fieldB1 \/ fieldB2).testStepSetOpsB1,
         (fieldB1 /\ fieldB2).testStepSetOpsB2,
         (fieldB1 - fieldB2).testStepSetOpsB3
  }

  asset SetOpsAssetB {
    | testStepSetOpsB1

    | testStepSetOpsB2

    | testStepSetOpsB3
  }
}

