        asset SubNetwork {
				| access
						-> accessPoint.communicate
        }
        asset AccessPoint {
				| communicate
						-> logicalNode.impact,
							server.communicate,
							ied.access,
							subNet.access,
							router.communicate,
							clock.communicate,
							clock.impact
